import React from "react";
import "./ProductDetailsScreen.scss";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";

function ProductDetailsScreen() {
  return (
    <div className="productDetailsScreen">
      <div className="productDetailsScreen-container">
        <div className="productDetailsScreen-header">
          <span>ASUS ROG</span>
        </div>
        <div className="productDetailsScreen-body">
        <Carousel
                      showStatus={false}
                      showArrows={false}
                      showIndicators={false}
                      // emulateTouch
                      // infiniteLoop
                      autoPlay={false}
                    >
                      {productById?.images.map((item, index) => (
                        <img
                          key={index}
                          className={`product__image__small`}
                          src={item}
                          alt={item}
                        />
                      ))}
                    </Carousel>
        </div>
      </div>
    </div>
  );
}

export default ProductDetailsScreen;
