import React from "react";
import "./CartItem.scss";
import CheckSharpIcon from "@material-ui/icons/CheckSharp";
import CancelSharpIcon from "@material-ui/icons/CancelSharp";
import AddIcon from "@material-ui/icons/Add";
import RemoveIcon from "@material-ui/icons/Remove";
import CurrencyFormat from "react-currency-format";

function CartItem({ cartItem }) {

  const chnageCartItemQuantityHandle = (value, id) => {
    dispatch(changeCartItemQuantity({ quantity: value, _id: id }));
  };

  const removeFromCartHandle = (cartItem) => {
    dispatch(removeFromCart(cartItem));
  };

  return (
    <div className="cartItem">
      <div className="cartItem-container">
        <div className="cartItem-info">
          <img src={cartItem.image} alt="" />
          <p>{cartItem.name}</p>
          <ul>
            <li>
              <CheckSharpIcon className="icon" />{" "}
              <span>{cartItem.processorModel}</span>
            </li>
            <li>
              <CheckSharpIcon className="icon" /> <span>{cartItem.memory}</span>
            </li>
            <li>
              <CheckSharpIcon className="icon" /> <span>{cartItem.video}</span>
            </li>
            <li>
              <CheckSharpIcon className="icon" />{" "}
              <span>{cartItem.storage}</span>
            </li>
          </ul>
        </div>
        <div className="cartItem-quantity">
          <RemoveIcon />
          <input type="number" min="1" defaultValue={cartItem.quantity} />
          <AddIcon />
        </div>
        <div className="cartItem-delete">
          <CancelSharpIcon />
        </div>
        <div className="cartItem-price">
          <CurrencyFormat
            renderText={(value) => (
              <>
                <span>{value}</span>
              </>
            )}
            decimalScale={2}
            value={cartItem.quantity * cartItem.price}
            displayType={"text"}
            thousandSeparator={true}
            prefix={"$"}
          />
        </div>
      </div>
    </div>
  );
}

export default CartItem;
